contract UserContract {
    uint256 postCounts;
    uint256 commentCounts;
    uint256 likeCounts;
    address owner;
    uint256 followersCount;
    address[] following;
    myLikeStruct[] myLikes;

    constructor(address walletId) {
        owner = walletId;
        postCounts = 0;
        commentCounts = 0;
        likeCounts = 0;
    }

    struct PostStruct {
        uint256 postId;
        string postContent;
        uint256 likeCount;
        uint256 commentCount;
        string timeStamp;
        string userName;
        bool isVerified;
        address commentContractAddress;
    }

    struct myLikeStruct {
        uint256 postId;
        string userName;
    }
    event Post(
        uint256 postId,
        string postContent,
        uint256 likeCount,
        uint256 commentCount,
        string timeStamp,
        string userName,
        bool isVerified,
        address commentContractAddress
    );

    event Like(uint256 likeCount);

    mapping(uint256 => PostStruct) public postStructs;

    // mapping(uint256 => CommentStruct) public commentStructs;

    function createPost(
        string memory _postContent,
        string memory _timeStamp,
        string memory userName,
        bool isVerified
    ) public {
        postStructs[postCounts].postId = postCounts;
        postStructs[postCounts].postContent = _postContent;
        postStructs[postCounts].likeCount = 0;
        postStructs[postCounts].commentCount = 0;
        postStructs[postCounts].timeStamp = _timeStamp;
        postStructs[postCounts].userName = userName;
        postStructs[postCounts].isVerified = isVerified;
        postStructs[postCounts].commentContractAddress = address(
            new Comments()
        );
        postCounts++;
        emit Post(
            postCounts,
            _postContent,
            0,
            0,
            _timeStamp,
            userName,
            isVerified,
            address(new Comments())
        );
    }

    function createComment(
        string memory _timeStamp,
        string memory _commentContent,
        string memory _commenterName,
        uint256 _postId
    ) public {
        Comments cc = Comments(postStructs[_postId].commentContractAddress);
        cc.createComment(_timeStamp, _commentContent, _commenterName);
    }

    function getComments(uint256 _postId)
        public
        view
        returns (Comments.CommentStruct[] memory)
    {
        Comments cc = Comments(postStructs[_postId].commentContractAddress);
        return cc.getComments();
    }

    function likePost(uint256 _postId) public {
        postStructs[_postId].likeCount++;
        emit Like(postStructs[_postId].likeCount);
    }

    function updateMylikes(uint256 _postId, string memory _userName) public {
        myLikes.push(myLikeStruct(_postId, _userName));
    }

    function followSomeone(address _walletId) public {
        following.push(_walletId);
    }

    function updateFollowersCount() public {
        followersCount++;
    }

    function getFollowersCount() public view returns (uint256) {
        return followersCount;
    }

    function getFollowing() public view returns (address[] memory) {
        return following;
    }

    function getPosts() public view returns (PostStruct[] memory) {
        PostStruct[] memory posts = new PostStruct[](postCounts);
        for (uint256 i = 0; i < postCounts; i++) {
            posts[i] = postStructs[i];
        }
        return posts;
    }

    // function getComments() public view returns (CommentStruct[] memory) {

    // }

    function getPostCount() public view returns (uint256) {
        return postCounts;
    }

    function getCommentCount() public view returns (uint256) {
        return commentCounts;
    }

    function getLikeCount() public view returns (uint256) {
        return likeCounts;
    }

    function getMyLikes() public view returns (myLikeStruct[] memory) {
        return myLikes;
    }
}

contract Comments {
    uint256 commentCounts;

    constructor() {
        commentCounts = 0;
    }

    struct CommentStruct {
        uint256 commentId;
        string commentContent;
        string timeStamp;
        string commenterName;
    }
    event Comment(
        string commentContent,
        string timeStamp,
        string commenterName
    );
    mapping(uint256 => CommentStruct) public commentStructs;

    function createComment(
        string memory _timeStamp,
        string memory _commentContent,
        string memory _commenterName
    ) public {
        commentStructs[commentCounts].commentId = commentCounts;
        commentStructs[commentCounts].commentContent = _commentContent;
        commentStructs[commentCounts].timeStamp = _timeStamp;
        commentStructs[commentCounts].commenterName = _commenterName;
        commentCounts++;
        emit Comment(_commentContent, _timeStamp, _commenterName);
    }

    function getComments() public view returns (CommentStruct[] memory) {
        CommentStruct[] memory comments = new CommentStruct[](commentCounts);
        for (uint256 i = 0; i < commentCounts; i++) {
            comments[i] = commentStructs[i];
        }
        return comments;
    }
}
